import { useReducer } from "react";
import "./style.css";

export default function App() {
	function reducer(state, action) {
		switch (action.type) {
			case "add":
				return state + action.data;
			case "minus":
				return state - action.data;
			case "multiply":
				return state * action.data;
			case "divide":
				return state / action.data;
			default:
				break;
		}
	}
	const initalState = { result: 0 };
	const [state, dispatch] = useReducer(reducer, initalState);

	return (
		<div className="calculator-grid">
			<div className="output">
				<div className="previous-operand"></div>
				<div className="current-operand"></div>
			</div>
			<button className="span-two">AC</button>
			<button>DEL</button>
			<button>รท</button>

			<button>1</button>
			<button>2</button>
			<button>3</button>
			<button>*</button>

			<button>4</button>
			<button>5</button>
			<button>6</button>
			<button>+</button>

			<button>7</button>
			<button>8</button>
			<button>9</button>
			<button>-</button>

			<button>.</button>
			<button>0</button>
			<button className="span-two">=</button>
		</div>
	);
}
