import { useReducer } from "react";
import "./style.css";

const ACTIONS = { ADD_DIGIT: "add-digit", CLEAR: "clear", DEL_DIGIT: "delete", DIVIDE: "divide" };

function reducer(state, { type, payload }) {
	switch (type) {
		case ACTIONS.ADD:
			return { previousOperand: state.previousOperand + 1 };
		case ACTIONS.MINUS:
			return state;
		case ACTIONS.MULTIPLY:
			return state;
		case ACTIONS.DIVIDE:
			return state;
		default:
			break;
	}
}
export default function App() {
	const [{ currentOperand, previousOperand, operation }, dispatch] = useReducer(reducer, {});

	return (
		<div className="calculator-grid">
			<div className="output">
				<div className="previous-operand">
					{previousOperand} {operation}
				</div>
				<div className="current-operand">{currentOperand}</div>
			</div>
			<button className="span-two">AC</button>
			<button>DEL</button>
			<button>รท</button>

			<button>1</button>
			<button>2</button>
			<button>3</button>
			<button>*</button>

			<button>4</button>
			<button>5</button>
			<button>6</button>
			<button onClick={() => dispatch({ type: ACTIONS.ADD, payload: {} })}>+</button>

			<button>7</button>
			<button>8</button>
			<button>9</button>
			<button>-</button>

			<button>.</button>
			<button>0</button>
			<button className="span-two">=</button>
		</div>
	);
}
