import React, { useState } from "react";
import "./index.css";

export default function Panel() {
	// State 1: Array to store operands, need 2 to start calculation, need only 1 if clear/del
	const [operandsArr, setOperand] = useState([0]);

	// State 2: Operator to store what operation to do
	// +: add || -: minus || x: mul || รท: div
	const [operator, setOperator] = useState("");

	// State 3: Calcute result when "=" is pressed
	let [display, setDisplay] = useState("0");

	//  callback for "=", calculation
	const handleEqClick = () => {
		const map = {
			add: () => operandsArr.shift() + operandsArr.shift(),
			minus: () => operandsArr.shift() - operandsArr.shift(),
			mul: () => operandsArr.shift() * operandsArr.shift(),
			div: () => operandsArr.shift() / operandsArr.shift(),
		};
		let result = map[operator]();
		setDisplay(result);
		setOperand([result]);
		setOperator("");
	};

	//  callback for operands input, store operands
	const handleInputClick = () => (e) => {
		const text = e.target.innerText;
		const value = parseInt(text);
		// Condition 1: 1st operand is 0 ==> replace
		if (operandsArr[0] === 0 && operandsArr.length === 1) {
			setDisplay(text);
			setOperand([value]);
		}
		// Condition 2: 1st operand is non-zero ==> string concat
		if (operandsArr[0] !== 0 && operandsArr.length === 1) {
			let newText = operandsArr[0] + text;
			setDisplay(newText);
			setOperand([parseInt(newText)]);
		}
	};

	//  callback for "AC", clear all state
	function clearAll() {
		return () => {
			setOperand([0]);
			setDisplay(0);
			setOperator("");
		};
	}

	return (
		<div className="panel-container">
			<div className="result">{display}</div>
			<button className="panel-item ac" onClick={clearAll()}>
				AC
			</button>
			<button className="panel-item " onClick={() => setOperator("del")}>
				DEL
			</button>
			<button className="panel-item " onClick={() => setOperator("div")}>
				รท
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				1
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				2
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				3
			</button>
			<button className="panel-item " onClick={() => setOperator("mul")}>
				x
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				4
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				5
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				6
			</button>
			<button className="panel-item " onClick={() => setOperator("add")}>
				+
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				7
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				8
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				9
			</button>
			<button className="panel-item " onClick={() => setOperator("minus")}>
				-
			</button>

			<button className="panel-item ">.</button>
			<button className="panel-item " onClick={handleInputClick()}>
				0
			</button>
			<button className="panel-item equal" onClick={() => handleEqClick()}>
				=
			</button>
		</div>
	);
}
