import React, { useState } from "react";
import "./index.css";

export default function Panel() {
	// State 1: Array to store operands, need 2 to start calculation, need only 1 if clear/del
	const [operandsArr, setOperand] = useState([]);

	// State 2: Operator to store what operation to do
	// +: add || -: minus || x: mul || รท: div
	const [operator, setOperator] = useState("");

	// State 3: Calcute result when "=" is pressed
	let [display, setDisplay] = useState("");

	// State 4: Flag for presence of 2nd operand
	let [hasSecond, setSecondFlag] = useState(false);
	/* ------------------------------------------------------------------------- */
	//  callback for operands input, store operands
	const handleInputClick = () => (e) => {
		const text = e.target.innerText;
		const value = text * 1;
		// Condition 1: No operands
		if (operandsArr.length === 0) {
			setDisplay(text);
			setOperand([value]);
		}
		// Condition 2: One operand
		if (operandsArr.length === 1) {
			// Condition 2.1: no operator
			if (!operator) {
				let newText = operandsArr[0] + text;
				setDisplay(newText);
				setOperand([newText * 1]);
			}
			// Condition 2.2: has operator
			else setOperand([operandsArr[0], value]);
		}
		// Condition 3: Two operands (must have operator)
		if (operandsArr.length === 2) {
			// Condition 3.1: hasSecond = false (only auto fill second when operator present)
			if (!hasSecond) {
				setDisplay(text);
				setOperand([operandsArr[0], value]);
				setSecondFlag(true);
			}
			// Condition 3.2: hasSecond = true (user input second operand)
			else {
				let newText = operandsArr[1] + text;
				setDisplay(newText);
				setOperand([operandsArr[0], newText * 1]);
			}
		}
	};

	// callback for operator click
	function handleOpClick(type) {
		setOperator(type);
		setOperand([operandsArr[0], operandsArr[0]]);
	}

	//  callback for "=", calculation
	function handleEqClick() {
		const map = {
			add: () => operandsArr.shift() + operandsArr.shift(),
			minus: () => operandsArr.shift() - operandsArr.shift(),
			mul: () => operandsArr.shift() * operandsArr.shift(),
			div: () => operandsArr.shift() / operandsArr.shift(),
		};
		let result = map[operator]() ?? 1;
		console.log(result);
		setDisplay(result.toString());
		setOperand([result]);
		setOperator("");
		setSecondFlag(false);
	}

	//  callback for "AC", clear all state
	function clearAll() {
		return () => {
			setOperand([]);
			setDisplay("");
			setOperator("");
			setSecondFlag(false);
		};
	}

	// callback for "del", backspace display and current operand
	function handleDelClick() {
		return () => {
			if (!display) return;
			let updateText = display.slice(0, -1);
			// when 1 operands
			setDisplay(updateText);
			// ("" * 1) => 0 ; parseInt("")=> NaN
			updateText ? setOperand([updateText * 1]) : setOperand([]);
		};
	}

	return (
		<div className="panel-container">
			<div className="result">{display ? display : "0"}</div>
			<button className="panel-item ac" onClick={clearAll()}>
				AC
			</button>
			<button className="panel-item " onClick={handleDelClick()}>
				DEL
			</button>
			<button className="panel-item " onClick={() => handleOpClick("div")}>
				รท
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				1
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				2
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				3
			</button>
			<button className="panel-item " onClick={() => handleOpClick("mul")}>
				x
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				4
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				5
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				6
			</button>
			<button className="panel-item " onClick={() => handleOpClick("add")}>
				+
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				7
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				8
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				9
			</button>
			<button className="panel-item " onClick={() => handleOpClick("minus")}>
				-
			</button>

			<button className="panel-item ">.</button>
			<button className="panel-item " onClick={handleInputClick()}>
				0
			</button>
			<button className="panel-item equal" onClick={() => handleEqClick()}>
				=
			</button>
		</div>
	);
}
