export default function evalutation(op, state) {
	// eslint-disable-next-line default-case
	switch (op) {
		case "รท":
			return {
				...state,
				currentOperand: (state.previousOperand * 1) / (state.currentOperand * 1),
				operation: op,
			};
		case "*":
			return {
				...state,
				currentOperand: state.previousOperand * 1 * (state.currentOperand * 1),
				operation: op,
			};
		case "+":
			return {
				...state,
				currentOperand: state.previousOperand * 1 + state.currentOperand * 1,
				operation: op,
			};
		case "-":
			return {
				...state,
				currentOperand: state.previousOperand * 1 - state.currentOperand * 1,
				operation: op,
			};
	}
}
