import { useReducer } from "react";
import DigitButton from "./DigitButton";
import OperationButton from "./OperationButton";
import evaluation from "./evaluation";
import "./style.css";

/* action type constant */
export const ACTIONS = {
	ADD_DIGIT: "add-digit",
	CLEAR: "clear",
	DEL_DIGIT: "delete-digit",
	CHOOSE_OPERATION: "choose-operation",
	EVALUATE: "evaluate",
};
/* Reducer function to update state object (all the logic)*/
function reducer(state, { type, payload }) {
	switch (type) {
		case ACTIONS.ADD_DIGIT: {
			// Edge Case 1: "0000000123", return current state
			if (state.currentOperand === "0" && payload.digit === "0") return state;
			// Edge Case 2: "10.........4", return current state
			if (payload.digit === "." && state.currentOperand?.includes(".")) return state;
			// Normal Case:
			return { ...state, currentOperand: `${state.currentOperand || ""}${payload.digit}` };
		}
		case ACTIONS.CHOOSE_OPERATION:
			// Edge Case 1: no operands
			// a == null is true if the value of a is null or undefined.
			if (state.currentOperand == null && state.previousOperand == null) return state;
			// Edge Case 2: operation auto evaluate (eg: 2+2+ become 4)
			// if (state.previousOperand && state.currentOperand && state.operation)
			// 	return evaluation(payload.operation, state);
			// // Edge Case 3:  operands change
			// if (state.previousOperand) return { ...state, operation: payload.operation };
			// Normal Case:
			if (state.previousOperand == null)
				return {
					...state,
					currentOperand: null,
					previousOperand: state.currentOperand,
					operation: payload.operation,
				};
			return {
				...state,
				previousOperand: evaluation(state),
				currentOperand: null,
				operation: payload.operation,
			};
		case ACTIONS.EVALUATE:
			return evaluation(payload.operation, state);
		case ACTIONS.CLEAR:
			return {};
		case ACTIONS.DEL_DIGIT:
			return { ...state, currentOperand: state.currentOperand?.slice(0, -1) };
		default:
			return state;
	}
}

/* evaluate function */
function evalutation(op, state) {
	// eslint-disable-next-line default-case
	switch (op) {
		case "รท":
			return {
				...state,
				currentOperand: (state.previousOperand * 1) / (state.currentOperand * 1),
				operation: op,
			};
		case "*":
			return {
				...state,
				currentOperand: state.previousOperand * 1 * (state.currentOperand * 1),
				operation: op,
			};
		case "+":
			return {
				...state,
				currentOperand: state.previousOperand * 1 + state.currentOperand * 1,
				operation: op,
			};
		case "-":
			return {
				...state,
				currentOperand: state.previousOperand * 1 - state.currentOperand * 1,
				operation: op,
			};
	}
}

/*  --------------UI component -------------------*/
export default function App() {
	// [state,dispatch]=useReducer(arg1, arg2)
	// -- arg1: defined reducer function
	// -- arg2: initial state
	// -- state: current state Object
	// -- dispatch: a built-in function used to dispatch an action object to reducer (arg1)
	const [{ currentOperand, previousOperand, operation }, dispatch] = useReducer(reducer, {});
	return (
		<div className="calculator-grid">
			<div className="output">
				<div className="previous-operand">
					{previousOperand} {operation}
				</div>
				<div className="current-operand">{currentOperand ?? 0}</div>
			</div>
			<button className="span-two" onClick={() => dispatch({ type: ACTIONS.CLEAR })}>
				AC
			</button>
			<button onClick={() => dispatch({ type: ACTIONS.DEL_DIGIT })}>DEL</button>
			<OperationButton dispatch={dispatch} operation="รท" />
			<DigitButton dispatch={dispatch} digit="1" />
			<DigitButton dispatch={dispatch} digit="2" />
			<DigitButton dispatch={dispatch} digit="3" />
			<OperationButton dispatch={dispatch} operation="*" />
			<DigitButton dispatch={dispatch} digit="4" />
			<DigitButton dispatch={dispatch} digit="5" />
			<DigitButton dispatch={dispatch} digit="6" />
			<OperationButton dispatch={dispatch} operation="+" />
			<DigitButton dispatch={dispatch} digit="7" />
			<DigitButton dispatch={dispatch} digit="8" />
			<DigitButton dispatch={dispatch} digit="9" />
			<OperationButton dispatch={dispatch} operation="-" />
			<DigitButton dispatch={dispatch} digit="." />
			<DigitButton dispatch={dispatch} digit="0" />
			<button
				className="span-two"
				onClick={() => dispatch({ type: ACTIONS.EVALUATE, payload: { operation } })}>
				=
			</button>
		</div>
	);
}
