import React, { useState } from "react";
import "./index.css";

export default function Panel() {
	// State 1: Array to store operands, need 2 to start calculation, need only 1 if clear/del
	const [operandsArr, setOperand] = useState([]);

	// State 2: Operator to store what operation to do
	// +: add || -: minus || x: mul || รท: div
	const [operator, setOperator] = useState("");

	// State 3: Calcute result when "=" is pressed
	const [display, setDisplay] = useState(0);
	const handleEqClick = () => {
		const map = {
			add: function () {
				return operandsArr[0] + operandsArr[1];
			},
		};
		setDisplay(map[operator]());
	};

	const handleInputClick = () => (e) => {
		const value = parseInt(e.target.innerText);
		setOperand([...operandsArr, value]);
		setDisplay(value);
	};
	return (
		<div className="panel-container">
			<div className="result">{display}</div>
			<button className="panel-item ac" onClick={() => setOperator("clear")}>
				AC
			</button>
			<button className="panel-item " onClick={() => setOperator("del")}>
				DEL
			</button>
			<button className="panel-item " onClick={() => setOperator("div")}>
				รท
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				1
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				2
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				3
			</button>
			<button className="panel-item " onClick={() => setOperator("mul")}>
				x
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				4
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				5
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				6
			</button>
			<button className="panel-item " onClick={() => setOperator("add")}>
				+
			</button>

			<button className="panel-item " onClick={handleInputClick()}>
				7
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				8
			</button>
			<button className="panel-item " onClick={handleInputClick()}>
				9
			</button>
			<button className="panel-item " onClick={() => setOperator("minus")}>
				-
			</button>

			<button className="panel-item ">.</button>

			<button className="panel-item " onClick={handleInputClick()}>
				0
			</button>
			<button className="panel-item equal" onClick={() => handleEqClick()}>
				=
			</button>
		</div>
	);
}
