import { useReducer } from "react";
import "./style.css";

/* action type constant */
const ACTIONS = {
	ADD_DIGIT: "add-digit",
	CLEAR: "clear",
	DEL_DIGIT: "delete-digit",
	CHOOSE_OPERATION: "choose-operation",
	EVALUATE: "evaluate",
};
/* Reducer function to update state object */
function reducer(state, { type, payload }) {
	console.log(state);
	switch (type) {
		case ACTIONS.ADD_DIGIT:
			return { ...state, currentOperand: payload };
		case ACTIONS.MINUS:
			return state;
		case ACTIONS.MULTIPLY:
			return state;
		case ACTIONS.DIVIDE:
			return state;
		default:
			return state;
	}
}

/*  UI component */
export default function App() {
	const [{ currentOperand, previousOperand, operation }, dispatch] = useReducer(reducer, {});

	return (
		<div className="calculator-grid">
			<div className="output">
				<div className="previous-operand">
					{previousOperand} {operation}
				</div>
				<div className="current-operand">{currentOperand}</div>
			</div>
			<button className="span-two">AC</button>
			<button>DEL</button>
			<button>รท</button>

			<button
				onClick={(e) =>
					dispatch({ type: ACTIONS.ADD_DIGIT, payload: e.target.innerText * 1 })
				}>
				1
			</button>
			<button>2</button>
			<button>3</button>
			<button>*</button>

			<button>4</button>
			<button>5</button>
			<button>6</button>
			<button onClick={() => dispatch({ type: ACTIONS.ADD, payload: {} })}>+</button>

			<button>7</button>
			<button>8</button>
			<button>9</button>
			<button>-</button>

			<button>.</button>
			<button>0</button>
			<button className="span-two">=</button>
		</div>
	);
}
